option.value:
@echo off echo ============================================ echo CAMBIO COMPLETO DE LOGO - PLAYTV+ echo ============================================ echo. echo Este script: echo 1. Genera iconos desde Logo PlayTV.png echo 2. Limpia el proyecto echo 3. Compila un APK limpio echo. pause
echo. echo [1/3] Generando iconos desde Logo PlayTV.png... echo. powershell -ExecutionPolicy Bypass -File "generar_iconos_directo.ps1" if errorlevel 1 ( echo. echo ERROR: Fallo al generar iconos pause exit /b 1 )
echo. echo [2/3] Limpiando proyecto... echo. call gradlew.bat clean if errorlevel 1 ( echo. echo ERROR: Fallo gradle clean pause exit /b 1 )
echo. echo [3/3] Compilando APK (esto tarda 2-3 minutos)... echo. call gradlew.bat assembleDebug if errorlevel 1 ( echo. echo ERROR: Fallo la compilacion pause exit /b 1 )
echo. echo ============================================ echo PROCESO COMPLETADO EXITOSAMENTE! echo ============================================ echo. echo APK generado en: echo app\build\outputs\apk\debug\app-debug.apk echo. echo ============================================ echo PASOS PARA INSTALAR Y VER EL LOGO echo ============================================ echo. echo 1. Ve a tu TV/TVBox echo - Configuracion ^> Aplicaciones ^> PlayTV+ echo - Presiona DESINSTALAR (no solo borrar datos) echo. echo 2. REINICIA tu TV/TVBox echo - Apaga el dispositivo echo - Desconecta el cable 30 segundos echo - Vuelve a encender echo. echo 3. Instala app-debug.apk echo - Copia el APK a USB echo - Instalalo en tu dispositivo echo. echo 4. El logo de PlayTV+ debe aparecer! echo. echo SIN EL REINICIO DEL DISPOSITIVO, echo EL LOGO NO CAMBIARA (cache del launcher) echo. pause

# Script para Redimensionar el Logo de PlayTV+ # Ejecutar en PowerShell
Write-Host "=== Generador de Iconos PlayTV+ ===" -ForegroundColor Cyan Write-Host ""
# Verificar si existe el logo original $logoOriginal = "playtv_logo.png" if (-not (Test-Path $logoOriginal)) { Write-Host "ERROR: No se encontró el archivo '$logoOriginal'" -ForegroundColor Red Write-Host "Por favor, coloca tu logo en el mismo directorio que este script." -ForegroundColor Yellow Write-Host "" Write-Host "ALTERNATIVA: Usa una herramienta online:" -ForegroundColor Green Write-Host " 1. https://icon.kitchen/" -ForegroundColor White Write-Host " 2. https://romannurik.github.io/AndroidAssetStudio/" -ForegroundColor White Write-Host " 3. https://appicon.co/" -ForegroundColor White Write-Host "" pause exit }
# Definir tamaños necesarios $sizes = @{ "mipmap-mdpi" = 48 "mipmap-hdpi" = 72 "mipmap-xhdpi" = 96 "mipmap-xxhdpi" = 144 "mipmap-xxxhdpi" = 192 }
Write-Host "Este script requiere ImageMagick instalado." -ForegroundColor Yellow Write-Host "Si no lo tienes, usa las herramientas online mencionadas arriba." -ForegroundColor Yellow Write-Host ""
# Verificar si ImageMagick está instalado $magickInstalled = Get-Command magick -ErrorAction SilentlyContinue
if (-not $magickInstalled) { Write-Host "ImageMagick no está instalado." -ForegroundColor Red Write-Host "" Write-Host "OPCIÓN 1 - Instalar ImageMagick:" -ForegroundColor Green Write-Host " 1. Descarga desde: https://imagemagick.org/script/download.php" -ForegroundColor White Write-Host " 2. Instala y reinicia PowerShell" -ForegroundColor White Write-Host " 3. Ejecuta este script nuevamente" -ForegroundColor White Write-Host "" Write-Host "OPCIÓN 2 - Usar herramienta online (MÁS FÁCIL):" -ForegroundColor Green Write-Host " 1. Ve a https://icon.kitchen/" -ForegroundColor White Write-Host " 2. Sube tu logo playtv_logo.png" -ForegroundColor White Write-Host " 3. Descarga el ZIP generado" -ForegroundColor White Write-Host " 4. Extrae en app/src/main/res/" -ForegroundColor White Write-Host "" pause exit }
Write-Host "ImageMagick detectado. Generando iconos..." -ForegroundColor Green Write-Host ""
# Crear directorios si no existen foreach ($folder in $sizes.Keys) { $path = Join-Path "app\src\main\res" $folder if (-not (Test-Path $path)) { New-Item -ItemType Directory -Path $path -Force | Out-Null } }
# Generar iconos en cada tamaño foreach ($folder in $sizes.Keys) { $size = $sizes[$folder] $outputPath = Join-Path "app\src\main\res\$folder" "ic_launcher.png"
Write-Host "Generando $folder (${size}x${size}px)..." -ForegroundColor White
magick convert $logoOriginal -resize ${size}x${size} $outputPath
# También generar versión redonda $outputPathRound = Join-Path "app\src\main\res\$folder" "ic_launcher_round.png" magick convert $logoOriginal -resize ${size}x${size} -background none ` "(" +clone -threshold -1 -draw "circle $($size/2),$($size/2) $($size/2),0" ")" ` -channel-fx "| gray=>alpha" $outputPathRound }
Write-Host "" Write-Host "¡Iconos generados exitosamente!" -ForegroundColor Green Write-Host "" Write-Host "Ahora ejecuta:" -ForegroundColor Cyan Write-Host " .\gradlew clean" -ForegroundColor White Write-Host " .\gradlew assembleDebug" -ForegroundColor White Write-Host "" Write-Host "Y reinstala la app en tu dispositivo." -ForegroundColor Yellow pause
